    html2canvas(this.refs.demo).then(function(canvas) {
        const imgUrl = canvas.toDataURL("image/png"); # 获取图片的url
        const elA = document.createElement("a");
        elA.download = +new Date() + ".png";
        elA.href = imgUrl ;
        elA.click();
        elA.remove();
    });

    //微信中不能下载图片，可以展示图片并提示用户长按保存图片
